function list=listfiles(dirpath)%given a directory path (dirpath), this function returns a cell with%pathnames leading to all%non-directory files within the directory specified by the input dirpathd=getdir(dirpath);list={};for a=1:length(d);%for each thing in the directory given    if ~d(a).isdir==1;%if it is not a directory        list{end+1}=fullfile(dirpath,d(a).name);%record the pathname to that file    endend